                    validateEwayFieldsImportColumns(requestJobj, columnConfig, dataMap, columnHeaderMap, columnCSVindexMap, customTablesArrayObj, dateFormat);
                    HashMap<String, Object> requestParams = new HashMap<String, Object>();
