                    reqParams.put("entitycolnum", reqParams.optInt("paymententitycolnum"));
                    reqParams.put("entityValue", reqParams.optString("paymententityValue"));
                    reqParams.put("dodocs", false);
                    reqParams.put("paymentdocs", true);
