                commData.put("coldata", jArr);  //Actual data
                commData.put("columns", jarrColumns); //Column Module Array
                commData.put("totalCount", result.getRecordTotalCount());
                commData.put("metaData", jMeta);

                jobj.put("valid", true);
                jobj.put(Constants.data, commData);
