                    KwlReturnObject currencyFilterResult = accountingHandlerDAOobj.getObject(KWLCurrency.class.getName(), currencyFilterForTrans);
                    KWLCurrency currencyFilter = (KWLCurrency) currencyFilterResult.getEntityList().get(0);
