                obj.put("termname", invoice.getCustomer() == null ? "" : ((invoice.getCustomer().getCreditTerm()) == null) ? "" : invoice.getCustomer().getCreditTerm().getTermname());
                obj.put("deleted", invoice.isDeleted());
